import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Download, ArrowDown, Github, Linkedin, Mail } from 'lucide-react';

const Hero = () => {
  const [currentText, setCurrentText] = useState(0);
  const texts = [
    "Software Developer",
    "Problem Solver", 
    "Innovator",
    "Creator"
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentText((prev) => (prev + 1) % texts.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [texts.length]);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.3,
        delayChildren: 0.2,
      },
    },
  };

  const itemVariants = {
    hidden: { y: 50, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.6,
        ease: "easeOut",
      },
    },
  };

  return (
    <section id="home" className="min-h-screen flex items-center justify-center relative overflow-hidden">
      {/* Background Elements */}
      <div className="absolute inset-0">
        <div className="absolute top-20 left-10 w-72 h-72 bg-accent-500/10 rounded-full blur-3xl animate-float"></div>
        <div className="absolute bottom-20 right-10 w-96 h-96 bg-primary-500/10 rounded-full blur-3xl animate-float" style={{ animationDelay: '2s' }}></div>
      </div>

      <div className="container-custom section-padding relative z-10">
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate="visible"
          className="text-center max-w-4xl mx-auto"
        >
          {/* Greeting */}
          <motion.div variants={itemVariants} className="mb-6">
            <span className="text-lg md:text-xl text-accent-500 font-mono">
              Hi, my name is
            </span>
          </motion.div>

          {/* Name */}
          <motion.h1 variants={itemVariants} className="text-5xl md:text-7xl lg:text-8xl font-bold mb-6">
            <span className="gradient-text">Krish Desai</span>
          </motion.h1>

          {/* Animated Tagline */}
          <motion.div variants={itemVariants} className="mb-8">
            <div className="text-2xl md:text-3xl lg:text-4xl font-semibold text-dark-300">
              <span className="text-white">I'm a </span>
              <motion.span
                key={currentText}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.5 }}
                className="gradient-text"
              >
                {texts[currentText]}
              </motion.span>
            </div>
          </motion.div>

          {/* Description */}
          <motion.p variants={itemVariants} className="text-lg md:text-xl text-dark-300 mb-12 max-w-2xl mx-auto leading-relaxed">
            A passionate software developer with a keen interest in creating innovative solutions 
            and leveraging technology to solve real-world problems. Currently pursuing the 
            International Baccalaureate program with a focus on computer science.
          </motion.p>

          {/* CTA Buttons */}
          <motion.div variants={itemVariants} className="flex flex-col sm:flex-row gap-4 justify-center items-center mb-16">
            <motion.a
              href="/ResumeCV.pdf"
              target="_blank"
              rel="noopener noreferrer"
              whileHover={{ scale: 1.05, y: -2 }}
              whileTap={{ scale: 0.95 }}
              className="px-8 py-4 bg-accent-500 hover:bg-accent-600 text-white rounded-full font-semibold flex items-center gap-2 transition-all duration-300 shadow-lg hover:shadow-accent-500/25"
            >
              <Download size={20} />
              Download Resume
            </motion.a>
            
            <motion.a
              href="#projects"
              whileHover={{ scale: 1.05, y: -2 }}
              whileTap={{ scale: 0.95 }}
              className="px-8 py-4 border-2 border-accent-500 text-accent-500 hover:bg-accent-500 hover:text-white rounded-full font-semibold flex items-center gap-2 transition-all duration-300"
            >
              View My Work
              <ArrowDown size={20} />
            </motion.a>
          </motion.div>

          {/* Social Links */}
          <motion.div variants={itemVariants} className="flex justify-center items-center gap-6">
            <motion.a
              href="https://github.com"
              target="_blank"
              rel="noopener noreferrer"
              whileHover={{ scale: 1.2, y: -5 }}
              whileTap={{ scale: 0.9 }}
              className="p-3 bg-dark-800 hover:bg-accent-500 rounded-full transition-all duration-300 group"
            >
              <Github size={24} className="group-hover:text-white transition-colors duration-300" />
            </motion.a>
            
            <motion.a
              href="https://linkedin.com"
              target="_blank"
              rel="noopener noreferrer"
              whileHover={{ scale: 1.2, y: -5 }}
              whileTap={{ scale: 0.9 }}
              className="p-3 bg-dark-800 hover:bg-accent-500 rounded-full transition-all duration-300 group"
            >
              <Linkedin size={24} className="group-hover:text-white transition-colors duration-300" />
            </motion.a>
            
            <motion.a
              href="mailto:krishhsdesai@gmail.com"
              whileHover={{ scale: 1.2, y: -5 }}
              whileTap={{ scale: 0.9 }}
              className="p-3 bg-dark-800 hover:bg-accent-500 rounded-full transition-all duration-300 group"
            >
              <Mail size={24} className="group-hover:text-white transition-colors duration-300" />
            </motion.a>
          </motion.div>
        </motion.div>
      </div>

      {/* Scroll Indicator */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 2, duration: 1 }}
        className="absolute bottom-8 left-1/2 transform -translate-x-1/2"
      >
        <motion.div
          animate={{ y: [0, 10, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
          className="flex flex-col items-center text-accent-500"
        >
          <ArrowDown size={24} />
          <span className="text-sm mt-2">Scroll to explore</span>
        </motion.div>
      </motion.div>
    </section>
  );
};

export default Hero;
